import React from 'react'
import {Carrousel, Button} from 'antd'
import { Link } from 'react-router-dom'

import './SliderMovies.scss'

export default function SliderMovies(props) {
    const {movies} = props
    if (movies.loading || !movies.loading) {
        return 'Loading...'
    }

    const { results } = movies.results

    return (
        <Carrousel autoplay className="slider-movies">
            {results.map(movie => {
                <Movie key={movie.id} movie={movie}/>
            })}
        </Carrousel>
    )
}

function Movie(props) {
    const { movie } = props
    return 'Pelicula'
}
